mapper.select:
select

from deposit_currency_order_user
where uid = #{uid}

	and asset_code = #{assetCode}

select

from deposit_currency_order_user

	
		and broker_id = #{brokerId}
	
	
		and id = #{id}
	
	
		and uid = #{uid}
	
	
		and txid = #{orderNo}
	
	
		and name = #{name}
	
	
		and account = #{account}
	
	
		and acnumber = #{acnumber}
	
	
		and status = #{status}
	

order by id
