mapper.select:
select

from trade_order

	
		and uid = #{uid}
	
	
		and status = #{status}

		select
		
		from trade_order
		
			
				and broker_id = #{brokerId}
			
			
				and symbol = #{symbol}
			
			
				and status = #{status}
			
			
				and order_type = #{type}
			

			
				and uid = #{uid}
			

			
				and account_id = #{accountId}
			
			
				and inner_order_no = #{innerOrderId}

select

from trade_order

	
		and broker_id = #{brokerId}
	
	
		and trade_flag = #{tradeFlag}
	
	
		and uid = #{uid}
	
	
		and symbol = #{symbol}
	
	
		and order_type = #{orderType}
	
	
		and status = #{status}
	
	
		and create_date > #{startTime}
	
	
		and create_date < #{endTime}
