mapper.select:
select
	
from administrators

	
		and admin_id = #{adminId}
	
	
		and mobile = #{mobile}
	
	
		and locked = #{locked}
	

order by
admin_id

select
count(admin_id) as num
from administrators

	
		and admin_id = #{uid}
	
	
		and mobile = #{mobile}
	
	
		and locked = #{locked}
