mapper.select:
select

from deposit_coin_order_user
where uid = #{uid,jdbcType=INTEGER}

	and asset_code = #{assetCode,jdbcType=VARCHAR}

select

from deposit_coin_order_user

	
		and broker_id = #{brokerId}
	
	
		and id = #{id}
	
	
		and uid = #{uid}
	
	
		and account = #{account}
	
	
		and asset_code = #{assetCode}
	
	
		and create_date >= #{beginTime}
	
	
		and create_date <= #{endTime}
	
	
		and account = #{email}
	
	
		and asset_status = #{assetStatus}
	
	
		and coin_address = #{address}
	

order by id

select

from deposit_coin_order_user
where
uid = #{uId}
	 and create_date >= #{fromDate} and create_date < #{toDate} 
order by id

select

from deposit_coin_order_user
where asset_status = #{status}

	and update_date > #{startDate}


	and update_date < #{endDate}


	and asset_code = #{assetCode}

	group by asset_code

select sum()
from deposit_coin_order_user
where asset_status = #{status}

	and update_date > #{startDate}


	and update_date < #{endDate}


	and asset_code = #{assetCode}

	group by asset_code
